<?phpsession_start();$guild = "Reckless Abandon";$realm = "Muradin";function isLoggedIn(){	return($_SESSION['status'] == 1);}function getConnection(){	$c = mysql_connect("dbhost","USER","pass");	if (isset($c))	{		mysql_select_db("USER");		return $c;	}else{		return false;	}}function head(){	echo "<html>\n";	echo "<head>\n";	echo "\t<title>Reckless Abandon of Muradin - Loot System</title>\n";	echo "\t<link rel=\"stylesheet\" type=\"text/css\" href=\"./style.css\" />";	echo "<script src=\"lootEntry.js\"></script>";	echo "<script src=\"ajax.js\"></script>";	echo "<script src=\"lootLog.js\"></script>";	echo "<script src=\"raiderManagement.js\"></script>";	echo "<script type=\"text/javascript\" src=\"keyboardShortcuts.js\" ></script>\n";	echo "</head>\n";	echo "<body onKeyPress=\"keyPressHandler(event);\">\n";}function tail(){	echo "<script src=\"http://static.wowhead.com/widgets/power.js\"></script>\n";	echo "</body>\n</html>\n";}function cleanSQLString($str){	return str_replace("\\'","''",$str); // This is... messy. Need to reassess this.}function playerInDatabase($name){    $q = "SELECT COUNT(*) AS NUMROWS FROM PLAYER WHERE CharacterName='" . cleanSQLString($name) . "';";    $r = mysql_query($q, getConnection());    $p = mysql_fetch_array($r);    if($p['NUMROWS'] == "0"){ return false;} else {return true;}}function classNameToCode($name){    $c = array();    $c['WARRIOR'] = 1;    $c['PALADIN'] = 2;    $c['HUNTER'] = 3;    $c['ROGUE'] = 4;    $c['PRIEST'] = 5;    $c['DEATHKNIGHT'] = 6;    $c['SHAMAN'] = 7;    $c['MAGE'] = 8;    $c['WARLOCK'] = 9;    $c['DRUID'] = 11;    return $c[$name];}function backButton(){    echo '<input type="button" value="Back" onClick="javascript:history.go(-1);" />';}?>